import React from 'react';
import styled from 'styled-components';

// Стилі для футера
const FooterContainer = styled.footer`
  background-color: #f8e1e9;
  padding: 20px 0;
  text-align: center;
`;

const FooterContent = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px 50px;
  border-top: 1px solid #ddd;

  @media (max-width: 768px) {
    flex-direction: column;
    text-align: center;
  }
`;

const Branding = styled.div`
  text-align: left;

  h4 {
    margin-bottom: 10px;
    font-size: 18px;
    color: #d81b60;
  }

  p {
    font-size: 14px;
    color: #666;
  }
`;

const Logo = styled.div`
  margin: 0 auto;

  img {
    width: 50px;
    height: 50px;
  }
`;

const SocialIcons = styled.div`
  display: flex;
  gap: 15px;
`;

const SocialIcon = styled.a`
  text-decoration: none;

  img {
    width: 24px;
    height: 24px;
  }
`;

const Copyright = styled.div`
  padding-top: 10px;
  font-size: 14px;
  color: #666;
  border-top: 1px solid #ddd;
`;

const SocialLinks = styled.div`
  font-size: 14px;
  color: #d81b60;
  display: flex;
  justify-content: center;
  gap: 20px;
  margin-top: 10px;

  a {
    color: #d81b60;
    text-decoration: none;
    font-weight: bold;
  }
`;

// Компонент Footer
const Footer = () => {
  return (
    <FooterContainer>
      <FooterContent>
        <Branding>
          <h4>Cosmetics Store</h4>
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc maximus, nulla ut commodo.</p>
        </Branding>
        <Logo>
          <img src="logo.png" alt="Logo" />
        </Logo>
        <SocialIcons>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/6/6f/Facebook_Logo_2020.png" alt="Facebook" />
          </SocialIcon>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/5/51/Twitter_logo.svg" alt="Twitter" />
          </SocialIcon>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/e/e9/Linkedin_icon.svg" alt="LinkedIn" />
          </SocialIcon>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/a/a7/Google_G_Logo.svg" alt="Google" />
          </SocialIcon>
        </SocialIcons>
      </FooterContent>
      <Copyright>© 2024 Cosmetics Store. All rights reserved.</Copyright>
      <SocialLinks>
        <a href="#">FB</a>
        <a href="#">TW</a>
        <a href="#">IG</a>
      </SocialLinks>
    </FooterContainer>
  );
};

export default Footer;
