import React, { useState } from 'react'; 
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { useNavigate } from 'react-router-dom';
import './CheckoutPage.css';

const CheckoutPage = () => {
  const [errorVisible, setErrorVisible] = useState(false); // Стан для помилки
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: {
      firstName: '',
      lastName: '',
      email: '',
      phone: '',
      address: ''
    },
    validationSchema: Yup.object({
      firstName: Yup.string().required('First name is required'),
      lastName: Yup.string().required('Last name is required'),
      email: Yup.string().email('Invalid email format').required('Email is required'),
      phone: Yup.string()
        .matches(/^\d+$/, 'Phone number must be a number')
        .required('Phone number is required'),
      address: Yup.string().required('Address is required')
    }),
    onSubmit: () => {
      setErrorVisible(false); // Сховати повідомлення помилки при успішному сабміті
      navigate('/success');
    },
    validateOnBlur: false, // Валідація не повинна відбуватись на кожному blur
    validateOnChange: false // Валідація тільки при сабміті
  });

  const handleErrorClose = () => {
    setErrorVisible(false); // Закриття повідомлення про помилку
  };

  return (
    <div className="checkout-container">
      <h1>Checkout</h1>

      {/* Повідомлення про помилку, яке з'являється після натискання кнопки Continue */}
      {errorVisible && (formik.errors.firstName || formik.errors.lastName || formik.errors.email || formik.errors.phone || formik.errors.address) && (
        <div className="error-message">
          <p>Oh snap! Change a few things up and try submitting again.</p>
          <button onClick={handleErrorClose} className="close-button">×</button>
        </div>
      )}
      
      <form onSubmit={(e) => { 
        e.preventDefault(); // Не оновлюємо сторінку
        formik.handleSubmit();
        if (formik.errors.firstName || formik.errors.lastName || formik.errors.email || formik.errors.phone || formik.errors.address) {
          setErrorVisible(true); // Показуємо помилку, якщо є неприпустимі значення
        }
      }}>
        <div className="flex-row">
          <div className="half-width">
            <label>First Name</label>
            <input
              type="text"
              {...formik.getFieldProps('firstName')}
            />
            {formik.touched.firstName && formik.errors.firstName && <div className="error">{formik.errors.firstName}</div>}
          </div>
          <div className="half-width">
            <label>Last Name</label>
            <input
              type="text"
              {...formik.getFieldProps('lastName')}
            />
            {formik.touched.lastName && formik.errors.lastName && <div className="error">{formik.errors.lastName}</div>}
          </div>
        </div>

        <div className="flex-row">
          <div className="half-width">
            <label>Email</label>
            <input
              type="email"
              {...formik.getFieldProps('email')}
            />
            {formik.touched.email && formik.errors.email && <div className="error">{formik.errors.email}</div>}
          </div>
          <div className="half-width">
            <label>Phone</label>
            <input
              type="text"
              {...formik.getFieldProps('phone')}
            />
            {formik.touched.phone && formik.errors.phone && <div className="error">{formik.errors.phone}</div>}
          </div>
        </div>

        <div>
          <label>Address</label>
          <input
            type="text"
            {...formik.getFieldProps('address')}
          />
          {formik.touched.address && formik.errors.address && <div className="error">{formik.errors.address}</div>}
        </div>

        <div className="actions">
          <button type="button" onClick={() => navigate(-1)} className="go-back-button">Go Back</button>
          <button type="submit" className="continue-button">Continue</button>
        </div>
      </form>
    </div>
  );
};

export default CheckoutPage;
