import React from 'react';
import styled from 'styled-components';

// Стилі для футера
const FooterContainer = styled.footer`
  background-color: #f8e1e9;
  padding: 20px 0;
  text-align: center;
`;

const FooterContent = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px 50px;
  @media (max-width: 768px) {
    flex-direction: column;
    text-align: center;
  }
`;

const Logo = styled.div`
  margin: 0 auto;
  img {
    width: 50px;
    height: 50px;
  }
`;

const SocialIcons = styled.div`
  display: flex;
  gap: 15px;
`;

const SocialIcon = styled.a`
  text-decoration: none;
  img {
    width: 24px;
    height: 24px;
  }
`;

const Copyright = styled.div`
  padding-top: 10px;
  font-size: 14px;
  color: #666;
  border-top: none;
`;

// Компонент Footer
const Footer = () => {
  return (
    <FooterContainer>
      <FooterContent>
        <Logo>
          <img src="logo.png" alt="Logo" />
        </Logo>
        <SocialIcons>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/6/6f/Facebook_Logo_2020.png" alt="Facebook" />
          </SocialIcon>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/5/51/Twitter_logo.svg" alt="Twitter" />
          </SocialIcon>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/e/e9/Linkedin_icon.svg" alt="LinkedIn" />
          </SocialIcon>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/a/a7/Google_G_Logo.svg" alt="Google" />
          </SocialIcon>
        </SocialIcons>
      </FooterContent>
      <Copyright>© 2024 Cosmetics Store. All rights reserved.</Copyright>
    </FooterContainer>
  );
};

export default Footer;
