import React from 'react';
import styled from 'styled-components';

const HeaderWrapper = styled.header`
  background-color: #ffffff; /* Хедер білого кольору */
  padding: 20px 0;
  display: flex;
  justify-content: center; /* Вирівнюємо елементи по центру */
  align-items: center;
  font-family: 'Montserrat', sans-serif;
`;

const Nav = styled.nav`
  flex: 1;
  ul {
    display: flex;
    justify-content: space-between; /* Простір між навігацією і логотипом */
    list-style-type: none;
    margin: 0;
    padding: 0;
    
    li {
      margin: 0 15px;
      cursor: pointer;
      font-size: 1.2em;
      color: #555;
    }
  }
`;

const Logo = styled.div`
  font-size: 2em;
  font-weight: bold;
  color: #d81b60;
  text-align: center;
  flex: 1;
`;

const Header = () => {
  return (
    <HeaderWrapper>
      <Nav>
        <ul>
          <li>Home</li>
          <li>Catalog</li>
          <li>Cart</li>
        </ul>
      </Nav>
      {/* Логотип по центру */}
      <Logo>Cosmetics Store</Logo>
    </HeaderWrapper>
  );
};

export default Header;
