import React from 'react';
import Slider from 'react-slick'; 
import 'slick-carousel/slick/slick.css'; 
import 'slick-carousel/slick/slick-theme.css'; 
import ProductTile from './ProductTile'; // Імпорт компонента ProductTile

const ProductSlider = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
  };

  return (
    <div style={{ padding: '50px' }}>
      <Slider {...settings}>
        <ProductTile title="Помада" description="Яскрава червона помада для сміливих образів" />
        <ProductTile title="Тональний крем" description="Натуральний відтінок для рівного тону шкіри" />
        <ProductTile title="Туш для вій" description="Надає об'єму і довжини вашим віям" />
        <ProductTile title="Пудра" description="Для матового покриття на весь день" />
        <ProductTile title="Парфуми" description="Елегантні парфуми для особливих випадків" />
        <ProductTile title="Блиск для губ" description="Блиск з ефектом зволоження" />
      </Slider>
    </div>
  );
};

export default ProductSlider;
