import React from 'react';
import styled from 'styled-components';
import logo from './images/logo.webp'; // Імпортуємо лого

// Стилі для футера
const FooterContainer = styled.footer`
  background-color: #f8e1e9;
  padding: 20px 0;
`;

const FooterContent = styled.div`
  display: flex;
  justify-content: center; /* Вирівнювання всіх елементів по центру */
  align-items: center;
  padding: 20px 50px;
  @media (max-width: 768px) {
    flex-direction: column;
    text-align: center;
  }
`;

const Logo = styled.div`
  margin-right: 50px; /* Додаємо відступ для логотипа, щоб не зливався з іконками */
  img {
    width: 50px;
    height: 50px;
  }
`;

const SocialIcons = styled.div`
  display: flex; /* Для вирівнювання іконок */
`;

const SocialIcon = styled.a`
  margin: 0 10px;
  text-decoration: none;
  display: inline-block;
  background-color: white;
  padding: 10px;
  border-radius: 50%; 
  box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1); 
  &:hover {
    background-color: #f4f4f4;
  }
  img {
    width: 24px;
    height: 24px;
  }
`;

const Copyright = styled.div`
  padding-top: 10px;
  font-size: 14px;
  color: #666;
  text-align: center;
`;

const Divider = styled.hr`
  border: 0;
  border-top: 1px solid black;
  margin: 20px 50px;
`;

const Footer = () => {
  return (
    <FooterContainer>
      <FooterContent>
        <Logo>
          <img src={logo} alt="Logo" /> {/* Використовуємо імпортовану змінну */}
        </Logo>
        <SocialIcons>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/1/16/Facebook-icon-1.png" alt="Facebook" />
          </SocialIcon>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/5/51/Twitter_logo.svg" alt="Twitter" />
          </SocialIcon>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/8/82/Telegram_logo.svg" alt="Telegram" />
          </SocialIcon>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/a/a5/Instagram_icon.png" alt="Instagram" />
          </SocialIcon>
        </SocialIcons>
      </FooterContent>
      
      <Divider />

      <Copyright>© 2024 Cosmetics Store. All rights reserved.</Copyright>
    </FooterContainer>
  );
};

export default Footer;
