import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import ProductTile from './ProductTile';
import styled from 'styled-components';

const Arrow = styled.div`
display: flex;
justify-content: center;
align-items: center;
background-color: #d81b60;
color: white;
width: 40px;
height: 40px;
border-radius: 50%;
cursor: pointer;
z-index: 2;
position: absolute;
top: 50%;
transform: translateY(-50%);
${({ $left }) => ($left ? 'left: -10px;' : 'right: 20px;')} 
`;

const SliderWrapper = styled.div`
padding: 50px;
position: relative;
max-width: 100%; 
overflow-x: hidden;

.slick-dots {
    bottom: -25px;
    display: flex !important;
    justify-content: center !important; 
}

.slick-list {
    padding-left: 70px; 
}
`;

const PrevArrow = (props) => {
const { onClick } = props;
return (
    <Arrow $left onClick={onClick}>
    &lt;
    </Arrow>
);
};

const NextArrow = (props) => {
const { onClick } = props;
return (
    <Arrow onClick={onClick}>
    &gt;
    </Arrow>
);
};

const ProductSlider = () => {
const settings = {
    dots: true, 
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    responsive: [
    {
        breakpoint: 768,
        settings: {
        slidesToShow: 1, 
        slidesToScroll: 1,
        },
    },
    {
        breakpoint: 1024,
        settings: {
        slidesToShow: 2, 
        slidesToScroll: 1,
        },
    },
    ],
};

return (
    <SliderWrapper>
    <Slider {...settings}>
        <ProductTile title="Помада" description="Яскрава червона помада для сміливих образів" />
        <ProductTile title="Тональний крем" description="Натуральний відтінок для рівного тону шкіри" />
        <ProductTile title="Туш для вій" description="Надає об'єму і довжини вашим віям" />
        <ProductTile title="Пудра" description="Для матового покриття на весь день" />
        <ProductTile title="Парфуми" description="Елегантні парфуми для особливих випадків" />
        <ProductTile title="Блиск для губ" description="Блиск з ефектом зволоження" />
    </Slider>
    </SliderWrapper>
);
};

export default ProductSlider;
