import React from 'react';
import ProductCard from './ProductCard'; // Для рендерингу кожного товару
import './catalog.css'; // Стилі для сторінки

const products = [
  {
    id: 1,
    name: 'Amazing Stuff',
    description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
    price: 2415,
    imageUrl: 'https://via.placeholder.com/150'
  },
  {
    id: 2,
    name: 'Amazing Stuff 2',
    description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
    price: 2450,
    imageUrl: 'https://via.placeholder.com/150'
  }
  // Додай більше продуктів, якщо потрібно
];

const Catalog = () => {
  return (
    <div className="catalog-container">
      <div className="filters-wrapper"> {/* Оновлено клас для фільтрів */}
        <select className="filter-select">
          <option>Filter 1</option>
        </select>
        <select className="filter-select">
          <option>Filter 2</option>
        </select>
        <select className="filter-select">
          <option>Filter 3</option>
        </select>
        <button className="filter-button">Apply</button> {/* Оновлено клас для кнопки */}
      </div>

      <div className="products-grid">
        {products.map(product => (
          <ProductCard key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
};

export default Catalog;
