import React, { useState } from 'react';
import MainBanner from '../mainbanner/MainBanner';
import ProductSlider from '../productslider/ProductSlider';

const Home = () => {
    const [showMore, setShowMore] = useState(false);

    const handleShowMore = () => {
        setShowMore(true);
    };

    return (
        <>
            <MainBanner />
            <ProductSlider />
            {!showMore ? (
                <button onClick={handleShowMore} className="view-more-button">
                    Переглянути більше
                </button>
            ) : (
                <div className="additional-content">
                    <h2>Додаткові продукти</h2>
                    <p>Ось деякі додаткові продукти, які можуть вас зацікавити:</p>
                    <p>Продукт 1: Це опис продукту 1, який може бути дуже корисним для вас.</p>
                    <p>Продукт 2: Це опис продукту 2, який також вам сподобається.</p>
                    {/* Додайте більше контенту або продуктів за потреби */}
                </div>
            )}
        </>
    );
};

export default Home;
