import React from 'react';
import Slider from 'react-slick';
import Header from './Header';
import MainBanner from './MainBanner';
import ProductTile from './ProductTile';
import Footer from './Footer';
import styled from 'styled-components';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const ProductListWrapper = styled.div`
display: flex;
  justify-content: center; /* Вирівнюємо елементи по центру */
  gap: 40px; /* Простір між картками */
padding: 50px;
`;

const Home = () => {
    const settings = {
      dots: true, // Показує індикатори
      infinite: true, // Безкінечне гортання
      speed: 500,
      slidesToShow: 3, // Скільки товарів показувати одночасно
      slidesToScroll: 1, // Скільки товарів прокручувати за раз
    };
  
    return (
      <>
        <Header />
        <MainBanner />
        <ProductListWrapper>
          <Slider {...settings}>
            <ProductTile title="Product 1" description="Description for product 1" />
            <ProductTile title="Product 2" description="Description for product 2" />
            <ProductTile title="Product 3" description="Description for product 3" />
            <ProductTile title="Product 4" description="Description for product 4" />
            <ProductTile title="Product 5" description="Description for product 5" />
            <ProductTile title="Product 6" description="Description for product 6" />
          </Slider>
        </ProductListWrapper>
        <Footer />
      </>
    );
  };
  
  export default Home;
