import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import ProductTile from './ProductTile';
import styled from 'styled-components';

const Arrow = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #d81b60;
  color: white;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  cursor: pointer;
  z-index: 2;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  ${({ isLeft }) => (isLeft ? 'left: -30px;' : 'right: -30px;')} /* Використовуємо "isLeft" для стилізації */
`;

// Ліва стрілка
const PrevArrow = (props) => {
  const { onClick } = props;
  return (
    <Arrow isLeft onClick={onClick}>
      &lt;
    </Arrow>
  );
};

// Права стрілка
const NextArrow = (props) => {
  const { onClick } = props;
  return (
    <Arrow onClick={onClick}>
      &gt;
    </Arrow>
  );
};


const ProductSlider = () => {
  const settings = {
    dots: false,  // Вимикаємо крапочки
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />
  };

  return (
    <div style={{ padding: '50px', position: 'relative' }}>
      <Slider {...settings}>
        <ProductTile title="Помада" description="Яскрава червона помада для сміливих образів" />
        <ProductTile title="Тональний крем" description="Натуральний відтінок для рівного тону шкіри" />
        <ProductTile title="Туш для вій" description="Надає об'єму і довжини вашим віям" />
        <ProductTile title="Пудра" description="Для матового покриття на весь день" />
        <ProductTile title="Парфуми" description="Елегантні парфуми для особливих випадків" />
        <ProductTile title="Блиск для губ" description="Блиск з ефектом зволоження" />
      </Slider>
    </div>
  );
};

export default ProductSlider;
