import React from 'react';
import styled from 'styled-components';

// Використовуємо деструктуризацію, щоб не передавати "image" в DOM
const TileWrapper = styled.div`
  border: 1px solid #ddd;
  padding: 20px;
  border-radius: 15px;
  text-align: center;
  width: 250px;
  margin: 15px;
  background-color: #fff;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
    box-shadow: 0 6px 15px rgba(0, 0, 0, 0.2);
  }
`;

// Тут ми використовуємо деструктуризацію, щоб не передавати image в DOM
const TileImage = styled.div.attrs(({ image }) => ({
  style: {
    backgroundImage: `url(${image})`,
  },
}))`
  background-color: #ffe4e1;
  height: 150px;
  margin-bottom: 15px;
  background-size: cover;
  background-position: center;
  border-radius: 10px;
`;

const Title = styled.h2`
  font-size: 1.5em;
  margin-bottom: 10px;
  color: #d81b60;
  font-family: 'Montserrat', sans-serif;
`;

const Description = styled.p`
  font-size: 1em;
  color: #777;
`;

const ProductTile = ({ title, description, image }) => {
  return (
    <TileWrapper>
      <TileImage image={image} />
      <Title>{title}</Title>
      <Description>{description}</Description>
    </TileWrapper>
  );
};

export default ProductTile;
