import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import ProductTile from './ProductTile';
import styled from 'styled-components';

import foundationImage from './images/foundation.jpg';
import mascaraImage from './images/mascara.jpg';
import bronzerImage from './images/bronzer.jpg';
import blushImage from './images/blush.jpg';
import oilImage from './images/oil.jpg';
import glossImage from './images/gloss.jpg';

const Arrow = styled.div`
display: flex;
justify-content: center;
align-items: center;
background-color: #d81b60;
color: white;
width: 40px;
height: 40px;
border-radius: 50%;
cursor: pointer;
z-index: 2;
position: absolute;
top: 50%;
transform: translateY(-50%);
${({ $left }) => ($left ? 'left: -10px;' : 'right: 10px;')} 
`;

const SliderWrapper = styled.div`
padding: 50px;
position: relative;
max-width: 100%; 
overflow-x: hidden;

.slick-dots {
    bottom: -25px;
    display: flex !important;
    justify-content: center !important; 
}

.slick-list {
    padding-left: 75px; 
}
`;

const PrevArrow = (props) => {
const { onClick } = props;
return (
    <Arrow $left onClick={onClick}>
    &lt;
    </Arrow>
);
};

const NextArrow = (props) => {
const { onClick } = props;
return (
    <Arrow onClick={onClick}>
    &gt;
    </Arrow>
);
};

const ProductSlider = () => {
const settings = {
    dots: true, 
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    responsive: [
    {
        breakpoint: 768,
        settings: {
        slidesToShow: 1, 
        slidesToScroll: 1,
        },
    },
    {
        breakpoint: 1024,
        settings: {
        slidesToShow: 2, 
        slidesToScroll: 1,
        },
    },
    ],
};

return (
    <SliderWrapper>
    <Slider {...settings}>
        <ProductTile title="Foundation" description="A natural shade for a flawless skin tone that hydrates and evens out the texture" image={foundationImage} />
        <ProductTile title="Mascara" description="Adds volume and length to lashes for a dramatic look" image={mascaraImage} />
        <ProductTile title="Bronzer" description="Adds a natural glow to contour and highlight cheekbones" image={bronzerImage} />
        <ProductTile title="Blush" description="A fresh blush that adds brightness and a healthy look to the face" image={blushImage} />
        <ProductTile title="Lip oil" description="Елегантні парфуми для особливих випадків" image={oilImage} />
        <ProductTile title="Lip gloss" description="Блиск з ефектом зволоження" image={glossImage} />
    </Slider>
    </SliderWrapper>
);
};

export default ProductSlider;
