import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { addToCart } from '../../redux/actions';
import './item.css';

const Item = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [item, setItem] = useState(null);
  const [selectedShade, setSelectedShade] = useState('Select');
  const [selectedType, setSelectedType] = useState('Select');
  const [quantity, setQuantity] = useState(1);  // Стан для кількості

  useEffect(() => {
    const fetchItem = async () => {
      try {
        const response = await fetch(`http://localhost:3000/api/products/${id}`);
        if (!response.ok) {
          throw new Error('Product not found');
        }
        const data = await response.json();
        setItem(data);
      } catch (error) {
        console.error('Error fetching product data:', error);
      }
    };

    fetchItem();
  }, [id]);

  if (!item) return <div>Loading...</div>;

  const handleAddToCart = () => {
    const product = {
      id: item.id,
      name: item.name,
      price: item.price,
      image: item.image,
      quantity: quantity,  // Передаємо кількість
      shade: selectedShade,
      type: selectedType
    };
    dispatch(addToCart(product));
    navigate('/cart');
  };

  return (
    <div className="item-page">
      <div className="item-image">
        <img src={item.image} alt={item.name} />
      </div>
      <div className="item-details">
        <h1 className="item-title">{item.name}</h1>
        <p className="item-description">{item.description}</p>
        <p className="item-price">Price: ${item.price}</p>
        <div className="item-actions">
          <div className="field-group">
            <label htmlFor="countable-field" className="field-label">Countable field</label>
            <input
              type="number"
              min="1"
              value={quantity}  // Значення з стану
              onChange={(e) => setQuantity(Number(e.target.value))}  // Оновлюємо кількість
              id="countable-field"
              className="countable-field"
            />
          </div>
          <div className="field-group">
            <label htmlFor="selectable-shade" className="field-label">Shade</label>
            <select
              id="selectable-shade"
              className="selectable-field"
              value={selectedShade}
              onChange={(e) => setSelectedShade(e.target.value)}
            >
              <option>Select</option>
              <option>Light</option>
              <option>Medium</option>
              <option>Dark</option>
            </select>
          </div>
          <div className="field-group">
            <label htmlFor="selectable-type" className="field-label">Type</label>
            <select
              id="selectable-type"
              className="selectable-field"
              value={selectedType}
              onChange={(e) => setSelectedType(e.target.value)}
            >
              <option>Select</option>
              <option>Cream</option>
              <option>Powder</option>
              <option>Gel</option>
              <option>Stick</option>
            </select>
          </div>
        </div>
        <div className="item-buttons">
          <button className="back-button" onClick={() => navigate(-1)}>Go back</button>
          <button className="add-button" onClick={handleAddToCart}>Add to cart</button>
        </div>
      </div>
    </div>
  );
};

export default Item;
