import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
import { ProductContext } from '../../context/ProductContext';


const Item = () => {
  const { id } = useParams();
  const { products } = useContext(ProductContext); // Отримуємо продукти з контексту
  const item = products.find(product => product.id === parseInt(id)); // Знаходимо товар за ID

  if (!item) {
    return <div>Item not found</div>; // Якщо товар не знайдено
  }

  return (
    <div>
      <h1>{item.name}</h1>
      <img src={item.image} alt={item.name} />
      <p>{item.description}</p>
      <p>Price: ${item.price}</p>
    </div>
  );
};

export default Item;
