import React from 'react';
import styled from 'styled-components';
import productImage from './images/dior.jpg'; // Імпортуй своє зображення

const BannerWrapper = styled.div`
  display: flex;
  align-items: center;
  padding: 50px;
  background-color: #f8e1e8;
  text-align: center; 
`;

const ImageWrapper = styled.div`
  width: 30%; /* Трохи збільшимо ширину */
  padding-right: 40px; /* Додамо більше відступу справа */

  img {
    width: 70%;
    height: 100%;
    border-radius: 10px;
  }
`;

const TextWrapper = styled.div`
  width: 70%; /* Відповідно, текст займатиме більше місця */
  padding-left: 20px; /* Додамо відступ зліва для більшої просторовості */
`;

const Heading = styled.h1`
  font-family: 'Playfair Display', serif;
  font-size: 2.5em;
  color: #d81b60;
  margin-bottom: 20px;
  text-align: center;
`;

const Description = styled.p`
  font-family: 'Montserrat', sans-serif;
  font-size: 1.2em;
  color: #555;
`;

const MainBanner = () => {
  return (
    <BannerWrapper>
      <ImageWrapper>
        <img src={productImage} alt="Product" />
      </ImageWrapper>
      <TextWrapper>
        <Heading>Discover Your Beauty</Heading>
        <Description>
          Luxurious cosmetics to enhance your natural glow. Experience the finest collection of beauty 
          products crafted with care, designed to inspire confidence and elevate your natural radiance.
          Whether you're looking for the perfect foundation, a bold lipstick, or the latest in skincare, 
          our selection is curated to meet the needs of every individual. Let your true beauty shine with
          products that not only look good but feel great on your skin. Explore our range and find the perfect 
          fit for your style and personality.
        </Description>
      </TextWrapper>
    </BannerWrapper>
  );
};

export default MainBanner;
