import React from 'react';

import Header from './Header';
import MainBanner from './MainBanner';
import ProductTile from './ProductTile';
import Footer from './Footer';


// Стилізація головного контейнера
import styled from 'styled-components';

const ProductListWrapper = styled.div`
display: flex;
justify-content: space-around;
padding: 50px;
`;

function App() {
return (
    <div className="App">
      {/* Додаємо компоненти у головний компонент App */}
    <Header />
    <MainBanner />

      {/* Створюємо список продуктів, використовуючи компонент ProductTile */}
    <ProductListWrapper>
        <ProductTile title="Помада" description="Яскрава червона помада для сміливих образів" />
        <ProductTile title="Тональний крем" description="Натуральний відтінок для рівного тону шкіри" />
        <ProductTile title="Туш для вій" description="Надає об'єму і довжини вашим віям" />
    </ProductListWrapper>

      {/* Додаємо компонент Footer */}
    <Footer />
    </div>
);
}

export default App;
