import React from 'react';
import styled from 'styled-components';

const HeaderWrapper = styled.header`
  background-color: #ffffff;
  padding: 20px 0;
  display: flex;
  justify-content: space-between; /* Вирівнюємо елементи шапки по бокам */
  align-items: center; /* Вирівнюємо елементи вертикально */
  font-family: 'Montserrat', sans-serif;
  max-width: 1200px;
  margin: 0 auto; /* Вирівнюємо шапку по центру сторінки */
`;

const Title = styled.h1`
  font-size: 2em;
  font-weight: bold;
  color: #d81b60;
  margin: 0;
`;

const Nav = styled.nav`
  ul {
    display: flex;
    list-style-type: none;
    padding: 0;
    margin: 0;
    gap: 20px; /* Відстань між пунктами меню */
    
    li {
      cursor: pointer;
      font-size: 1.2em;
      color: #555;
    }
  }
`;

const Logo = styled.div`
  img {
    height: 40px;
  }
`;

const Header = () => {
  return (
    <HeaderWrapper>
      {/* Меню навігації зліва */}
      <Nav>
        <ul>
          <li>Home</li>
          <li>Catalog</li>
          <li>Cart</li>
        </ul>
      </Nav>

      {/* Заголовок по центру */}
      <Title>Cosmetics Store</Title>

      {/* Логотип справа */}
      <Logo>
        <img src="path_to_your_logo.png" alt="Logo" />
      </Logo>
    </HeaderWrapper>
  );
};

export default Header;
