import React from 'react';
import styled from 'styled-components';

const HeaderWrapper = styled.header`
background-color: #ffffff;
padding: 20px;
display: flex;
  justify-content: space-between; /* Вирівнюємо лого і навігацію з протилежних сторін */
align-items: center;
font-family: 'Montserrat', sans-serif;
`;

const Logo = styled.div`
font-size: 1.8em;
font-weight: bold;
color: #d81b60;
`;

const Nav = styled.nav`
ul {
    display: flex;
    list-style-type: none;
    padding: 0;
    margin: 0;
    gap: 30px; /* Відстань між пунктами меню */
    
    li {
    cursor: pointer;
    font-size: 1.2em;
    color: #555;
    }
}
`;

const Header = () => {
 return (
    <HeaderWrapper>
      {/* Навігація вирівняна ліворуч */}
      <Nav>
        <ul>
          <li>Home</li>
          <li>Catalog</li>
          <li>Cart</li>
        </ul>
      </Nav>

      {/* Логотип вирівняний праворуч */}
      <Logo>
        <img src="path_to_your_logo.png" alt="Logo" style={{ height: '40px' }} />
      </Logo>
    </HeaderWrapper>
  );
};

export default Header;
