import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import './header.css';
import logoImage from '../../images/logo.'; // Імпорт зображення логотипу

const Header = () => {
  return (
    <header className="header-wrapper">
      <div className="logo">
        <img src={logoImage} alt="Cosmetics Store Logo" className="logo-image" /> {/* Логотип */}
      </div>
      <nav className="nav">
        <ul className="nav-list">
          <li><Link to="/">Home</Link></li>
          <li><Link to="/catalog">Catalog</Link></li>
          <li>Cart</li>
        </ul>
      </nav>
    </header>
  );
};

export default Header;
