import React, { useState, useEffect } from 'react';
import ProductCard from './ProductCard'; 
import PrimaryButton from '../PrimaryButton'; 
import Select from '../Select'; 
import { getAllProducts } from '../../services/productApi';
import Spinner from '../loader/Spinner';
import './catalog.css';

const Catalog = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [filter, setFilter] = useState('All');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  // Завантаження продуктів при першому завантаженні сторінки
  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    setLoading(true);
    try {
      const filters = {};
      if (filter !== 'All') filters.type = filter;
      if (minPrice) filters.minPrice = minPrice;
      if (maxPrice) filters.maxPrice = maxPrice;
      if (searchQuery) filters.search = searchQuery;

      console.log('Запит до API з фільтрами:', filters);
      const { data } = await getAllProducts(filters);
      setProducts(data);
    } catch (error) {
      console.error("Error fetching products:", error);
    } finally {
      setLoading(false);
    }
  };

  // Обробник для кнопки "Apply", що застосовує фільтри
  const handleApplyFilters = () => {
    fetchProducts();
  };

  const filterOptions = ['All', 'Makeup', 'Lip Care', 'Perfumes', 'Skincare'];

  return (
    <div className="catalog-container">
      <div className="filters-wrapper">
        <div className="filter-group">
          <Select 
            options={filterOptions} 
            onChange={(e) => setFilter(e.target.value)} 
            className="filter-select"
          />
          <input
            type="number"
            placeholder="Min Price"
            value={minPrice}
            onChange={(e) => setMinPrice(e.target.value)}
            className="price-input"
            min="0"
          />
          <input
            type="number"
            placeholder="Max Price"
            value={maxPrice}
            onChange={(e) => setMaxPrice(e.target.value)}
            className="price-input"
            min="0"
          />
        </div>
        <div className="search-group">
          <div className="search-wrapper">
            <input
              type="text"
              placeholder="Search"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)} 
              className="search-input"
            />
            <span className="search-icon">&#128269;</span>
          </div>
        </div>
        <div className="apply-button-wrapper">
          <PrimaryButton label="Apply" onClick={handleApplyFilters} />
        </div>
      </div>

      {loading ? (
        <Spinner />
      ) : (
        <div className="products-grid">
          {products.length > 0 ? (
            products.map(product => (
              <ProductCard key={product.id} product={product} />
            ))
          ) : (
            <p>Нічого не знайдено</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Catalog;
