import React from 'react';
import styled from 'styled-components';

// Стилі для футера
const FooterContainer = styled.footer`
  padding: 20px;
  background-color: #f9f9f9;
  text-align: center;
  border-top: 1px solid #ddd;
`;

const FooterContent = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px 0;
`;

const Branding = styled.div`
  text-align: left;

  h4 {
    margin-bottom: 10px;
    font-size: 18px;
  }

  p {
    font-size: 14px;
    color: #666;
  }
`;

const Logo = styled.div`
  margin: 0 auto;
`;

const SocialIcons = styled.div`
  display: flex;
  gap: 15px;
`;

const SocialIcon = styled.a`
  text-decoration: none;

  img {
    width: 24px;
    height: 24px;
  }
`;

const Copyright = styled.div`
  padding-top: 10px;
  font-size: 14px;
  color: #666;
  border-top: 1px solid #ddd;
`;

// Компонент Footer
const Footer = () => {
  return (
    <FooterContainer>
      <FooterContent>
        <Branding>
          <h4>Branding stuff</h4>
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc maximus, nulla ut commodo</p>
        </Branding>
        <Logo>
          {/* Можеш додати зображення логотипу тут */}
          <img src="logo.png" alt="Logo" style={{ width: '50px', height: '50px' }} />
        </Logo>
        <SocialIcons>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/6/6f/Facebook_Logo_2020.png" alt="Facebook" />
          </SocialIcon>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/5/51/Twitter_logo.svg" alt="Twitter" />
          </SocialIcon>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/e/e9/Linkedin_icon.svg" alt="LinkedIn" />
          </SocialIcon>
          <SocialIcon href="#">
            <img src="https://upload.wikimedia.org/wikipedia/commons/a/a7/Google_G_Logo.svg" alt="Google" />
          </SocialIcon>
        </SocialIcons>
      </FooterContent>
      <Copyright>2024 IoT © Copyright all rights reserved, bla bla</Copyright>
    </FooterContainer>
  );
};

export default Footer;
