import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import ProductTile from './ProductTile';
import styled from 'styled-components';

// Кастомні стилі для стрілочок
const Arrow = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #d81b60;
  color: white;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  cursor: pointer;
  z-index: 2;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  ${props => props.left ? 'left: -30px;' : 'right: -30px;'}
`;

const SliderWrapper = styled.div`
  padding: 50px;
  position: relative;
  display: flex;
  justify-content: center; /* Центрування слайдера */
`;

const TileWrapper = styled.div`
  width: 280px; /* Встановлюємо однакову ширину для всіх карток */
  margin: 0 auto; /* Центрування картки в контейнері */
  display: flex;
  justify-content: center;
`;

const PrevArrow = (props) => {
  const { onClick } = props;
  return (
    <Arrow left onClick={onClick}>
      &lt;
    </Arrow>
  );
};

const NextArrow = (props) => {
  const { onClick } = props;
  return (
    <Arrow onClick={onClick}>
      &gt;
    </Arrow>
  );
};

const ProductSlider = () => {
  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    centerMode: true, // Додаємо режим центрування слайдів
    centerPadding: '0' // Без додаткових відступів
  };

  return (
    <SliderWrapper>
      <Slider {...settings}>
        <TileWrapper>
          <ProductTile title="Блиск для губ" description="Блиск з ефектом зволоження" />
        </TileWrapper>
        <TileWrapper>
          <ProductTile title="Помада" description="Яскрава червона помада для сміливих образів" />
        </TileWrapper>
        <TileWrapper>
          <ProductTile title="Тональний крем" description="Натуральний відтінок для рівного тону шкіри" />
        </TileWrapper>
        <TileWrapper>
          <ProductTile title="Пудра" description="Для матового покриття на весь день" />
        </TileWrapper>
        <TileWrapper>
          <ProductTile title="Парфуми" description="Елегантні парфуми для особливих випадків" />
        </TileWrapper>
        <TileWrapper>
          <ProductTile title="Блиск для губ" description="Блиск з ефектом зволоження" />
        </TileWrapper>
      </Slider>
    </SliderWrapper>
  );
};

export default ProductSlider;
