import React from 'react';
import styled from 'styled-components';

const BannerWrapper = styled.div`
  padding: 50px 20px;
  text-align: center;
  background-color: #fce4ec;
`;

const Heading = styled.h1`
  font-size: 2.5em;
  margin-bottom: 10px;
  font-family: 'Playfair Display', serif;
  color: #d81b60;
`;

const SubHeading = styled.p`
  font-size: 1.2em;
  color: #555;
  margin-bottom: 20px;
`;

const MainBanner = () => {
  return (
    <BannerWrapper>
      <Heading>Discover Your Beauty</Heading>
      <SubHeading>Luxurious cosmetics to enhance your natural glow</SubHeading>
    </BannerWrapper>
  );
};

export default MainBanner;
