import React, { useState } from 'react';
import ProductCard from './ProductCard'; 
import PrimaryButton from '../PrimaryButton'; 
import Select from '../Select'; 
import './catalog.css';

import foundationImage from '../../images/foundation.jpg';
import mascaraImage from '../../images/mascara.jpg';
import bronzerImage from '../../images/bronzer.jpg';
import blushImage from '../../images/blush.jpg';
// Додайте інші зображення, як у 8 лабораторній...

const Catalog = () => {
  // Тимчасово жорстко закодовані дані продуктів
  const [products] = useState([
    { id: 1, name: 'Foundation', description: 'A natural shade...', price: 300, image: foundationImage, type: 'Makeup' },
    { id: 2, name: 'Mascara', description: 'Adds volume...', price: 150, image: mascaraImage, type: 'Makeup' },
    { id: 3, name: 'Bronzer', description: 'Adds a natural glow...', price: 500, image: bronzerImage, type: 'Makeup' },
    { id: 4, name: 'Blush', description: 'A fresh blush...', price: 100, image: blushImage, type: 'Makeup' },
    // Додайте інші продукти, як у 8 лабораторній
  ]);

  const [filter, setFilter] = useState('All');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  // Фільтрація продуктів
  const filteredProducts = products.filter(product => {
    const matchesCategory = filter === 'All' || product.type === filter;
    const matchesPrice = (!minPrice || product.price >= minPrice) &&
                         (!maxPrice || product.price <= maxPrice);
    const matchesSearchQuery = product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                               product.description.toLowerCase().includes(searchQuery.toLowerCase());

    return matchesCategory && matchesPrice && matchesSearchQuery; 
  });

  const filterOptions = ['All', 'Makeup', 'Lip Care', 'Perfumes', 'Skincare'];

  return (
    <div className="catalog-container">
      <div className="filters-wrapper">
        <div className="filter-group">
          <Select 
            options={filterOptions} 
            onChange={(e) => setFilter(e.target.value)} 
            className="filter-select"
          />
          <input
            type="number"
            placeholder="Min Price"
            value={minPrice}
            onChange={(e) => setMinPrice(e.target.value)}
            className="price-input"
          />
          <input
            type="number"
            placeholder="Max Price"
            value={maxPrice}
            onChange={(e) => setMaxPrice(e.target.value)}
            className="price-input"
          />
        </div>
        <div className="search-group">
          <div className="search-wrapper">
            <input
              type="text"
              placeholder="Search"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)} 
              className="search-input"
            />
            <span className="search-icon">&#128269;</span>
          </div>
        </div>
      </div>

      <div className="products-grid">
        {filteredProducts.map(product => (
          <ProductCard key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
};

export default Catalog;
