import React from 'react';
import styled from 'styled-components';
import bannerImage from './images/dior.jpg'; 
const BannerWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 60px;
  background-color: #ffe4e1; /* Задаємо рожевий фон */
  text-align: center;
`;

const Heading = styled.h1`
  font-family: 'Playfair Display', serif;
  font-size: 2.5em;
  font-weight: bold;
  color: #d81b60;
`;

const Description = styled.p`
  font-family: 'Montserrat', sans-serif;
  font-size: 1.2em;
  color: #555;
  max-width: 600px;
  margin: 0 auto;
`;

const MainBanner = () => {
  return (
    <BannerWrapper>
      <div>
        <Heading>Discover Your Beauty</Heading>
        <Description>
          Luxurious cosmetics to enhance your natural glow. Lorem ipsum dolor sit amet,
          consectetur adipiscing elit. Nunc maximus, nulla ut commodo sagittis.
        </Description>
      </div>
    </BannerWrapper>
  );
};

export default MainBanner;
