import React from 'react';
import styled from 'styled-components';

const HeaderWrapper = styled.header`
  background-color: #ffffff;
  padding: 20px;
  text-align: center; /* Центруємо заголовок "Cosmetics Store" */
  font-family: 'Montserrat', sans-serif;
`;

const Title = styled.h1`
  font-size: 2em;
  font-weight: bold;
  color: #d81b60;
  margin-bottom: 20px;
`;

const NavBar = styled.div`
  display: flex;
  justify-content: space-between; /* Вирівнюємо меню зліва, логотип справа */
  align-items: center;
`;

const Nav = styled.nav`
  ul {
    display: flex;
    list-style-type: none;
    padding: 0;
    margin: 0;
    gap: 30px;
    
    li {
      cursor: pointer;
      font-size: 1.2em;
      color: #555;
    }
  }
`;

const Logo = styled.div`
  img {
    height: 40px;
  }
`;

const Header = () => {
  return (
    <HeaderWrapper>
      {/* Заголовок сайту по центру */}
      <Title>Cosmetics Store</Title>
      
      {/* Меню навігації зліва і логотип справа */}
      <NavBar>
        <Nav>
          <ul>
            <li>Home</li>
            <li>Catalog</li>
            <li>Cart</li>
          </ul>
        </Nav>
        
        <Logo>
          <img src="path_to_your_logo.png" alt="Logo" />
        </Logo>
      </NavBar>
    </HeaderWrapper>
  );
};

export default Header;
