const SliderWrapper = styled.div`
  padding: 50px;
  position: relative;
  padding-left: 160px;
  max-width: 100%; /* Обмежуємо максимальну ширину */
  overflow-x: hidden; /* Прибираємо горизонтальний скрол */
`;

const ProductSlider = () => {
  const settings = {
    dots: true,  // Відображаємо крапки
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />,
    responsive: [  // Додаємо адаптивність для мобільних пристроїв
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,  // Показуємо один слайд на малих екранах
          slidesToScroll: 1,
          infinite: true,
        },
      },
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,  // Показуємо два слайди на середніх екранах
          slidesToScroll: 1,
          infinite: true,
        },
      },
    ],
  };

  return (
    <SliderWrapper>
      <Slider {...settings}>
        <ProductTile title="Помада" description="Яскрава червона помада для сміливих образів" />
        <ProductTile title="Тональний крем" description="Натуральний відтінок для рівного тону шкіри" />
        <ProductTile title="Туш для вій" description="Надає об'єму і довжини вашим віям" />
        <ProductTile title="Пудра" description="Для матового покриття на весь день" />
        <ProductTile title="Парфуми" description="Елегантні парфуми для особливих випадків" />
        <ProductTile title="Блиск для губ" description="Блиск з ефектом зволоження" />
      </Slider>
    </SliderWrapper>
  );
};

export default ProductSlider;
