import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import foundationImage from '../../images/foundation.jpg';
import mascaraImage from '../../images/mascara.jpg';
import bronzerImage from '../../images/bronzer.jpg';
import blushImage from '../../images/blush.jpg';
import oilImage from '../../images/oil.jpg';
import glossImage from '../../images/gloss.jpg';
import highlighterImage from '../../images/highlighter.jpg';
import concealerImage from '../../images/concealer.jpg';
import './item.css';

const Item = () => {
  const { id } = useParams();
  const [products] = useState([
    { id: 1, name: 'Foundation', description: 'A natural shade for a flawless skin tone that hydrates.', price: 300, image: foundationImage },
    { id: 2, name: 'Mascara', description: 'Adds volume and length to lashes for a dramatic look.', price: 150, image: mascaraImage },
    { id: 3, name: 'Bronzer', description: 'Adds a natural glow to contour and highlight cheekbones.', price: 500, image: bronzerImage },
    { id: 4, name: 'Blush', description: 'A fresh blush that adds brightness and a healthy look to the face.', price: 100, image: blushImage },
    { id: 5, name: 'Lip oil', description: 'Moisturizes and nourishes the lips, leaving a light glowing effect.', price: 250, image: oilImage },
    { id: 6, name: 'Lip gloss', description: 'Lip gloss with a moisturizing effect that adds soft shine and care.', price: 200, image: glossImage },
    { id: 7, name: 'Highlighter', description: 'Adds a natural feel to the skin, enhancing the contours.', price: 350, image: highlighterImage },
    { id: 8, name: 'Concealer', description: 'Safely covers dark stains, stains and imperfections in the skin.', price: 280, image: concealerImage },
  ]);

  const item = products.find(product => product.id === parseInt(id));

  if (!item) {
    return <div>Item not found</div>;
  }

  return (
    <div className="item-page">
      <div className="item-image">
        <img src={item.image} alt={item.name} />
      </div>
      <div className="item-details">
        <h1 className="item-title">{item.name}</h1>
        <p className="item-description">{item.description}</p>
        <p className="item-price">Price: ${item.price}</p>
        <div className="item-actions">
  <div className="field-group">
    <label htmlFor="countable-field" className="field-label">Countable field</label>
    <input type="number" min="1" defaultValue="1" id="countable-field" className="countable-field" />
  </div>
  
  <div className="field-group">
    <label htmlFor="selectable-field" className="field-label">Selectable Field</label>
    <select id="selectable-field" className="selectable-field">
      <option>Select</option>
    </select>
  </div>
</div>
        <div className="item-buttons">
          <Link to="/catalog">
            <button className="back-button">Go back</button>
          </Link>
          <button className="add-button">Add to cart</button>
        </div>
      </div>
    </div>
  );
};

export default Item;
