import React, { useContext } from 'react';
import { Link } from 'react-router-dom'; // Додано імпорт для маршрутизації
import ProductCard from './ProductCard'; 
import PrimaryButton from '../PrimaryButton'; 
import Select from '../Select'; 
import './catalog.css'; 
import { ProductContext } from '../context/ProductContext'; // Переконайтеся, що шлях правильний


const Catalog = () => {
  const { products } = useContext(ProductContext); 
  const filterOptions = ['Filter 1', 'Filter 2', 'Filter 3'];

  return (
    <div className="catalog-container">
      <div className="filters-wrapper">
        <Select options={filterOptions} />
        <Select options={filterOptions} />
        <Select options={filterOptions} />
        <PrimaryButton label="Apply" onClick={() => alert('Filters applied!')} />
      </div>

      <div className="products-grid">
        {products.map(product => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} />
            <h2>{product.name}</h2>
            <p>{product.description}</p>
            <p>Ціна: ${product.price}</p>
            {/* Додаємо посилання на сторінку товару */}
            <Link to={`/catalog/${product.id}`}>
              <PrimaryButton label="View more" />
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Catalog;
